mapper.select:
select 

from Schedule s
left join StartURL u on s.startURLId = u.id


        u.siteId like CONCAT('%','${match}','%' )



    order by ${sort}

limit #{start}, #{limit}

mapper.update:
update Schedule set
startURLId = #{startURLId},
jobId = #{jobId},
schedule = #{schedule},
scheduleType = #{scheduleType}
where id = #{id}
